import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Doughnut } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';

// Register ChartJS components
ChartJS.register(ArcElement, Tooltip, Legend);

const Users = () => {
  const [users, setUsers] = useState([]); // Users list state
  const [statusData, setStatusData] = useState(null); // Active vs Inactive chart data

  useEffect(() => {
    // Fetch users from the backend
    axios.get('http://localhost:5000/api/users')
      .then(response => {
        const userList = response.data;
        setUsers(userList);

        // Calculate active and inactive users
        const statusCount = userList.reduce(
          (acc, user) => {
            if (user.isActive) {
              acc.active += 1;
            } else {
              acc.inactive += 1;
            }
            return acc;
          },
          { active: 0, inactive: 0 }
        );

        // Prepare Doughnut chart data
        setStatusData({
          labels: ['Active', 'Inactive'],
          datasets: [
            {
              label: 'User Status',
              data: [statusCount.active, statusCount.inactive],
              backgroundColor: ['#4CAF50', '#F44336'], // Green for Active, Red for Inactive
            },
          ],
        });
      })
      .catch(error => console.error('Error fetching user data:', error));
  }, []);

  return (
    <div style={{ padding: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h1>User List</h1>
      <table border="1" style={{ width: '80%', marginBottom: '30px', textAlign: 'center' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Active</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role.name}</td>
              <td>
                <input type="checkbox" checked={user.isActive} readOnly />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>User Status: Active vs Inactive</h2>
      <div style={{ width: '60%', height: '400px', display: 'flex', justifyContent: 'center' }}>
        {statusData ? <Doughnut data={statusData} /> : <p>Loading chart...</p>}
      </div>
    </div>
  );
};

export default Users;
