import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Box, ThemeProvider, createTheme, CssBaseline } from '@mui/material';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import Users from './pages/Users';
import Roles from './pages/Roles';
import Home from './pages/Home';

function App() {
    const [darkMode, setDarkMode] = useState(false); // Default theme is light

    // Toggle theme function
    const handleThemeToggle = () => {
        setDarkMode(!darkMode);
    };

    // Create dynamic theme
    const theme = createTheme({
        palette: {
            mode: darkMode ? 'dark' : 'light', // Switch between light and dark mode
            primary: {
                main: '#1976d2',
            },
            secondary: {
                main: '#dc004e',
            },
        },
    });

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            <Router>
                <Box sx={{ display: 'flex' }}>
                    <Sidebar handleThemeToggle={handleThemeToggle} darkMode={darkMode} />
                    <Box
                        component="main"
                        sx={{ flexGrow: 1, bgcolor: 'background.default', padding: 3 }}
                    >
                        <Navbar handleThemeToggle={handleThemeToggle} />
                        <Routes>
                            <Route path="/home" element={<Home />} />
                            <Route path="/users" element={<Users />} />
                            <Route path="/roles" element={<Roles />} />
                        </Routes>
                    </Box>
                </Box>
            </Router>
        </ThemeProvider>
    );
}

export default App;
