import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { TextField, Button, Select, MenuItem, FormControl, InputLabel, Grid, Typography, Box } from '@mui/material';
import { Link } from 'react-router-dom';

// Helper functions to interact with the API
const fetchUsers = async () => {
  const res = await fetch('http://localhost:5000/api/users');
  if (!res.ok) throw new Error('Failed to fetch users');
  return res.json();
};

const fetchRoles = async () => {
  const res = await fetch('http://localhost:5000/api/roles');
  if (!res.ok) throw new Error('Failed to fetch roles');
  return res.json();
};

const createOrUpdateUser = async ({ userId, userData }) => {
  const url = userId
    ? `http://localhost:5000/api/users/${userId}`
    : 'http://localhost:5000/api/users';
  const method = userId ? 'PUT' : 'POST';

  const res = await fetch(url, {
    method,
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(userData),
  });
  if (!res.ok) throw new Error('Failed to save user');
  return res.json();
};

const deleteUser = async (userId) => {
  const res = await fetch(`http://localhost:5000/api/users/${userId}`, {
    method: 'DELETE',
  });
  if (!res.ok) throw new Error('Failed to delete user');
  return res.json();
};

const Users = () => {
  const queryClient = useQueryClient();
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    firstName: '',
    lastName: '',
    phone: '',
    role: '',
    status: 'Active',
  });
  const [editingUser, setEditingUser] = useState(null);

  // Fetch users and roles
  const { data: users, isLoading: usersLoading, isError: usersError } = useQuery({
    queryKey: ['users'],
    queryFn: fetchUsers,
  });

  const { data: roles, isLoading: rolesLoading, isError: rolesError } = useQuery({
    queryKey: ['roles'],
    queryFn: fetchRoles,
  });

  // Mutation to create or update users
  const saveUserMutation = useMutation({
    mutationFn: createOrUpdateUser,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['users'] }); // Refetch users after mutation
      resetForm();
    },
  });

  // Mutation to delete users
  const deleteUserMutation = useMutation({
    mutationFn: deleteUser,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['users'] }); // Refetch users after deletion
    },
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    saveUserMutation.mutate({
      userId: editingUser?._id,
      userData: formData,
    });
  };

  const handleEdit = (user) => {
    setFormData({
      username: user.username,
      email: user.email,
      firstName: user.firstName,
      lastName: user.lastName,
      phone: user.phone,
      role: user.role?._id || '',
      status: user.status,
    });
    setEditingUser(user);
  };

  const handleDelete = (userId) => {
    deleteUserMutation.mutate(userId);
  };

  const resetForm = () => {
    setFormData({
      username: '',
      email: '',
      firstName: '',
      lastName: '',
      phone: '',
      role: '',
      status: 'Active',
    });
    setEditingUser(null);
  };

  if (usersLoading || rolesLoading) return <Typography>Loading...</Typography>;
  if (usersError || rolesError) return <Typography>Error loading data.</Typography>;

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        {editingUser ? 'Edit User' : 'Create a New User'}
      </Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              fullWidth
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              fullWidth
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              label="First Name"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              fullWidth
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Last Name"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              fullWidth
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Phone"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              fullWidth
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth required>
              <InputLabel>Role</InputLabel>
              <Select
                name="role"
                value={formData.role}
                onChange={handleChange}
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                {roles.map((role) => (
                  <MenuItem key={role._id} value={role._id}>
                    {role.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
        </Grid>
        <Button type="submit" variant="contained" color="primary" sx={{ marginTop: '20px' }}>
          {editingUser ? 'Update User' : 'Create User'}
        </Button>
      </form>

      <Typography variant="h4" gutterBottom sx={{ marginTop: 5 }}>
        User List
      </Typography>
      <Box>
        {users?.length > 0 ? (
          <Grid container spacing={3}>
            {users.map((user) => (
              <Grid item xs={12} sm={6} key={user._id}>
                <Box sx={{ border: '1px solid #ccc', padding: 2 }}>
                  <Typography variant="h6">
                    {user.firstName} {user.lastName}
                  </Typography>
                  <p>
                    <strong>Username:</strong> {user.username}
                  </p>
                  <p>
                    <strong>Email:</strong> {user.email}
                  </p>
                  <p>
                    <strong>Phone:</strong> {user.phone}
                  </p>
                  <p>
                    <strong>Role:</strong> {user.role ? user.role.name : 'No role assigned'}
                  </p>
                  <p>
                    <strong>Status:</strong> {user.status}
                  </p>
                  <Button variant="contained" color="secondary" onClick={() => handleEdit(user)}>
                    Edit
                  </Button>
                  <Link
                    to="#"
                    onClick={() => handleDelete(user._id)}
                    style={{
                      textDecoration: 'none',
                      color: 'white',
                      backgroundColor: 'red',
                      marginLeft: '15px',
                      padding: '6px 16px',
                      borderRadius: '4px',
                      fontWeight: 'bold',
                    }}
                  >
                    Delete
                  </Link>
                </Box>
              </Grid>
            ))}
          </Grid>
        ) : (
          <p>No users found.</p>
        )}
      </Box>
    </div>
  );
};

export default Users;
