import React from 'react';
import { Box, List, ListItem, ListItemText, Drawer, IconButton } from '@mui/material';
import { Link } from 'react-router-dom';
import { Brightness4, Brightness7 } from '@mui/icons-material'; // Icons for light/dark mode
import './Sidebar.css';

const Sidebar = ({ handleThemeToggle, darkMode }) => {
  return (
    <Box sx={{ position: 'relative', height: '100vh' }}>
      <Drawer variant="permanent" sx={{ width: 240, flexShrink: 0 }}>
        <Box
          sx={{
            width: 240,
            height: '100%',
            background: 'linear-gradient(45deg, #004e92, #00c6ff)',
            padding: '20px',
            color: 'white',
          }}
        >
          <List>
            <ListItem component={Link} to="/home" className="nav-link">
              <ListItemText primary="Home" />
            </ListItem>
            <ListItem component={Link} to="/about" className="nav-link">
              <ListItemText primary="About" />
            </ListItem>
            <ListItem component={Link} to="/contact" className="nav-link">
              <ListItemText primary="Contact" />
            </ListItem>
          </List>
          {/* Theme Toggle Button */}
          <IconButton
            color="inherit"
            onClick={handleThemeToggle}
            sx={{
              position: 'absolute',
              bottom: '20px',
              left: '50%',
              transform: 'translateX(-50%)',
            }}
          >
            {darkMode ? <Brightness7 /> : <Brightness4 />}
          </IconButton>
        </Box>
      </Drawer>
    </Box>
  );
};

export default Sidebar;
