import React, { useState } from 'react';
import { Box, List, ListItem, ListItemText, Drawer, Switch, FormControlLabel } from '@mui/material';
import { Link } from 'react-router-dom';
import './Sidebar.css';

const Sidebar = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true); // State to control sidebar visibility

  // Toggle sidebar visibility (changes the width)
  const handleSidebarToggle = (event) => {
    setIsSidebarOpen(event.target.checked); // Toggle sidebar open/close
  };

  return (
    <Box sx={{ position: 'relative', height: '100vh' }}>
      {/* Sidebar Drawer */}
      <Drawer
        variant="permanent"
        sx={{
          width: isSidebarOpen ? 260 : 20, // Sidebar width is 240px when open, 20px when closed
          flexShrink: 0,
          transition: 'width 0.3s ease', // Smooth transition for width change
          overflow: 'hidden', // Hide content when sidebar is narrow
          backgroundColor: isSidebarOpen ? 'linear-gradient(45deg, #004e92, #00c6ff)' : 'transparent', // Make the sidebar transparent when closed
        }}
      >
        <Box
          sx={{
            width: '100%',
            height: '100%',
            padding: isSidebarOpen ? '20px' : '5px', // Adjust padding based on sidebar state
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between', // Push the toggle to the bottom when sidebar is closed
            opacity: isSidebarOpen ? 1 : 0, // Make everything inside the sidebar transparent when closed
            transition: 'opacity 0.3s ease', // Smooth transition for opacity change
          }}
        >
          {/* Show Links when sidebar is open */}
          {isSidebarOpen && (
            <List>
              <ListItem component={Link} to="/home" className="nav-link">
                <ListItemText primary="Home" />
              </ListItem>
              <ListItem component={Link} to="/about" className="nav-link">
                <ListItemText primary="About" />
              </ListItem>
              <ListItem component={Link} to="/contact" className="nav-link">
                <ListItemText primary="Contact" />
              </ListItem>
            </List>
          )}

          {/* Sidebar visibility toggle */}
          <Box sx={{ marginTop: 'auto', padding: '10px', display: 'flex', justifyContent: 'center' }}>
            <Box
              sx={{
                backgroundColor: 'black', // Black box around the toggle
                padding: '5px', // Padding inside the black box
                borderRadius: '5px', // Rounded corners for the box
              }}
            >
              <FormControlLabel
                control={
                  <Switch
                    checked={isSidebarOpen} // If sidebar is open, the switch is on
                    onChange={handleSidebarToggle}
                    name="sidebarToggle"
                    color="primary"
                  />
                }
                label="" // Remove the label text
                sx={{
                  visibility: isSidebarOpen ? 'visible' : 'visible', // Keep the switch visible
                }}
              />
            </Box>
          </Box>
        </Box>
      </Drawer>
    </Box>
  );
};

export default Sidebar;
