import React, { useState } from 'react';
import { Box, List, ListItem, ListItemText, Drawer, Switch, FormControlLabel, IconButton } from '@mui/material';
import { Link } from 'react-router-dom';
import { ArrowForward, ArrowBack } from '@mui/icons-material'; // Import icons for toggling
import './Sidebar.css';

const Sidebar = () => {
  const [brightness, setBrightness] = useState(100);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true); // State to control sidebar visibility

  // Toggle brightness state
  const handleBrightnessChange = (event) => {
    setBrightness(event.target.checked ? 50 : 100); // Adjust brightness as per the toggle
  };

  // Toggle sidebar visibility
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen); // Toggle the sidebar visibility
  };

  return (
    <Box sx={{ position: 'relative', height: '100vh' }}>
      {/* Sidebar Drawer */}
      <Drawer
        variant="permanent"
        open={isSidebarOpen} // Use open prop to control visibility
        sx={{
          width: isSidebarOpen ? 240 : 0, // Set width to 0 when sidebar is closed
          flexShrink: 0,
          transition: 'width 0.3s ease', // Smooth transition for width change
          overflow: 'hidden', // Hide the content when sidebar is closed
        }}
      >
        <Box
          sx={{
            width: '100%',
            height: '100%',
            backgroundColor: 'linear-gradient(45deg, #004e92, #00c6ff)',
            padding: '20px',
            filter: `brightness(${brightness}%)`, // Adjust the brightness of the page
            transition: 'filter 0.3s ease', // Smooth transition for brightness change
          }}
        >
          <List>
            <ListItem component={Link} to="/home" className="nav-link">
              <ListItemText primary="Home" />
            </ListItem>
            <ListItem component={Link} to="/about" className="nav-link">
              <ListItemText primary="About" />
            </ListItem>
            <ListItem component={Link} to="/contact" className="nav-link">
              <ListItemText primary="Contact" />
            </ListItem>
          </List>

          {/* Brightness Toggle */}
          <Box sx={{ marginTop: 'auto', padding: '10px' }}>
            <FormControlLabel
              control={
                <Switch
                  checked={brightness === 50} // If brightness is at 50%, the switch is on
                  onChange={handleBrightnessChange}
                  name="brightnessToggle"
                  color="primary"
                />
              }
              label="Dark Mode"
            />
          </Box>
        </Box>
      </Drawer>

      {/* Arrow Button to Show Sidebar when Hidden */}
      {!isSidebarOpen && (
        <IconButton
          sx={{
            position: 'absolute',
            top: '50%',
            left: 0,
            transform: 'translateY(-50%)',
            zIndex: 1,
            backgroundColor: '#004e92',
            color: '#fff',
            borderRadius: '50%',
            boxShadow: 3,
          }}
          onClick={toggleSidebar}
        >
          <ArrowForward /> {/* Arrow to open the sidebar */}
        </IconButton>
      )}

      {/* Arrow Button to Hide Sidebar when Visible */}
      {isSidebarOpen && (
        <IconButton
          sx={{
            position: 'absolute',
            top: '50%',
            right: -30,
            transform: 'translateY(-50%)',
            zIndex: 1,
            backgroundColor: '#004e92',
            color: '#fff',
            borderRadius: '50%',
            boxShadow: 3,
          }}
          onClick={toggleSidebar}
        >
          <ArrowBack /> {/* Arrow to close the sidebar */}
        </IconButton>
      )}

      {/* Sidebar Toggle Switch */}
      <Box sx={{ position: 'absolute', top: 20, left: 20 }}>
        <FormControlLabel
          control={
            <Switch
              checked={isSidebarOpen} // Sidebar is open if the switch is checked
              onChange={toggleSidebar}
              color="primary"
            />
          }
          label="Toggle Sidebar"
        />
      </Box>
    </Box>
  );
};

export default Sidebar;
