import React, { useState } from 'react';
import { Button, TextField, MenuItem, Select, InputLabel, FormControl, Grid, Box, Typography } from '@mui/material';
import { Link } from 'react-router-dom';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';

// Fetch roles
const fetchRoles = async () => {
  const response = await fetch('http://localhost:5000/api/roles');
  if (!response.ok) {
    throw new Error('Error fetching roles');
  }
  return response.json();
};

// Create or update role
const createOrUpdateRole = async (formData, editingRole) => {
  const url = editingRole
    ? `http://localhost:5000/api/roles/${editingRole._id}`
    : 'http://localhost:5000/api/roles';
  const method = editingRole ? 'PUT' : 'POST';
  
  const response = await fetch(url, {
    method,
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(formData),
  });
  
  if (!response.ok) {
    throw new Error('Error saving role');
  }
  
  return response.json();
};

// Delete role
const deleteRole = async (id) => {
  const response = await fetch(`http://localhost:5000/api/roles/${id}`, { method: 'DELETE' });
  if (!response.ok) {
    throw new Error('Error deleting role');
  }
};

const Roles = () => {
  const [formData, setFormData] = useState({
    name: '',
    permissions: [],
  });
  const [editingRole, setEditingRole] = useState(null);

  const permissionsList = ['read', 'write', 'delete', 'manage']; // Example permissions list

  // Fetch roles with useQuery
  const { data: roles = [], isLoading, isError, error } = useQuery(['roles'], fetchRoles);

  // React Query client for cache manipulation
  const queryClient = useQueryClient();

  // Mutation for creating/updating a role
  const { mutate: saveRole } = useMutation(createOrUpdateRole, {
    onSuccess: () => {
      // Invalidate roles cache to refetch the updated data
      queryClient.invalidateQueries(['roles']);
    },
  });

  // Mutation for deleting a role
  const { mutate: removeRole } = useMutation(deleteRole, {
    onSuccess: () => {
      // Invalidate roles cache to refetch the updated data
      queryClient.invalidateQueries(['roles']);
    },
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handlePermissionsChange = (e) => {
    const { value } = e.target;
    setFormData({ ...formData, permissions: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    saveRole(formData, editingRole);
    setFormData({
      name: '',
      permissions: [],
    });
    setEditingRole(null);
  };

  const handleEdit = (role) => {
    setEditingRole(role);
    setFormData({
      name: role.name,
      permissions: role.permissions || [],
    });
  };

  const handleDelete = (id) => {
    removeRole(id);
  };

  if (isLoading) return <Typography>Loading roles...</Typography>;
  if (isError) return <Typography>Error: {error.message}</Typography>;

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        {editingRole ? 'Edit Role' : 'Create a New Role'}
      </Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Role Name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              fullWidth
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth required>
              <InputLabel>Permissions</InputLabel>
              <Select
                multiple
                name="permissions"
                value={formData.permissions}
                onChange={handlePermissionsChange}
                label="Permissions"
              >
                {permissionsList.map((permission, index) => (
                  <MenuItem key={index} value={permission}>
                    {permission}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
        </Grid>
        <Button type="submit" variant="contained" color="primary" sx={{ marginTop: '20px' }}>
          {editingRole ? 'Update Role' : 'Create Role'}
        </Button>
      </form>

      <Typography variant="h4" gutterBottom sx={{ marginTop: 5 }}>
        Role List
      </Typography>
      <Box>
        {roles.length > 0 ? (
          <Grid container spacing={3}>
            {roles.map((role) => (
              <Grid item xs={12} sm={6} key={role._id}>
                <Box sx={{ border: '1px solid #ccc', padding: 2 }}>
                  <Typography variant="h6">{role.name}</Typography>
                  <p>
                    <strong>Permissions:</strong> {(role.permissions || []).join(', ')}
                  </p>
                  <Button variant="contained" color="secondary" onClick={() => handleEdit(role)}>
                    Edit
                  </Button>
                  <Link
                    to="#"
                    onClick={() => handleDelete(role._id)}
                    style={{
                      textDecoration: 'none',
                      color: 'white',
                      backgroundColor: 'red',
                      position: 'absolute',
                      marginLeft: '15px',
                      padding: '6px 16px',
                      borderRadius: '4px',
                      fontWeight: 'bold',
                      display: 'inline-block',
                    }}
                  >
                    Delete
                  </Link>
                </Box>
              </Grid>
            ))}
          </Grid>
        ) : (
          <p>No roles found.</p>
        )}
      </Box>
    </div>
  );
};

export default Roles;
