import React, { useState } from 'react';
import { Box, List, ListItem, ListItemText, Drawer, Switch, FormControlLabel } from '@mui/material';
import { Link } from 'react-router-dom';
import './Sidebar.css';

const Sidebar = () => {
  const [brightness, setBrightness] = useState(100);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true); // State to control sidebar visibility

  // Toggle brightness state
  const handleBrightnessChange = (event) => {
    setBrightness(event.target.checked ? 50 : 100); // Adjust brightness as per the toggle
  };

  // Toggle sidebar visibility (changes the width)
  const handleSidebarToggle = (event) => {
    setIsSidebarOpen(event.target.checked); // Toggle sidebar open/close
  };

  return (
    <Box sx={{ position: 'relative', height: '100vh' }}>
      {/* Sidebar Drawer */}
      <Drawer
        variant="permanent"
        sx={{
          width: isSidebarOpen ? 240 : 60, // Sidebar is wide when open (240px), narrow (60px) when closed
          flexShrink: 0,
          transition: 'width 0.3s ease', // Smooth transition for width change
          overflow: 'hidden', // Hide content when sidebar is narrow
        }}
      >
        <Box
          sx={{
            width: '100%',
            height: '100%',
            backgroundColor: 'linear-gradient(45deg, #004e92, #00c6ff)',
            padding: '20px',
            filter: `brightness(${brightness}%)`, // Adjust the brightness of the page
            transition: 'filter 0.3s ease', // Smooth transition for brightness change
          }}
        >
          {/* Sidebar Content */}
          <List>
            <ListItem component={Link} to="/home" className="nav-link">
              <ListItemText primary="Home" sx={{ visibility: isSidebarOpen ? 'visible' : 'hidden' }} />
            </ListItem>
            <ListItem component={Link} to="/about" className="nav-link">
              <ListItemText primary="About" sx={{ visibility: isSidebarOpen ? 'visible' : 'hidden' }} />
            </ListItem>
            <ListItem component={Link} to="/contact" className="nav-link">
              <ListItemText primary="Contact" sx={{ visibility: isSidebarOpen ? 'visible' : 'hidden' }} />
            </ListItem>
          </List>

          {/* Brightness Toggle */}
          <Box sx={{ marginTop: 'auto', padding: '10px' }}>
            <FormControlLabel
              control={
                <Switch
                  checked={brightness === 50} // If brightness is at 50%, the switch is on
                  onChange={handleBrightnessChange}
                  name="brightnessToggle"
                  color="primary"
                />
              }
              label="Dark Mode"
            />
          </Box>

          {/* Sidebar visibility toggle */}
          <Box sx={{ marginTop: '10px' }}>
            <FormControlLabel
              control={
                <Switch
                  checked={isSidebarOpen} // If sidebar is open, the switch is on
                  onChange={handleSidebarToggle}
                  name="sidebarToggle"
                  color="primary"
                />
              }
              label="Show Sidebar"
            />
          </Box>
        </Box>
      </Drawer>
    </Box>
  );
};

export default Sidebar;
