import React, { useEffect, useState } from 'react';
import { PieChart, Pie, Cell, Legend, Tooltip, ResponsiveContainer } from 'recharts';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip as BarTooltip, Legend as BarLegend } from 'recharts';

const Graph = () => {
  const [userData, setUserData] = useState([]);
  const [rolesData, setRolesData] = useState([]);
  
  // Fetch user data
  useEffect(() => {
    fetch('/api/users')  // Your backend endpoint for users
      .then((response) => response.json())
      .then((data) => setUserData(data))
      .catch((error) => console.error('Error fetching user data:', error));
  }, []);

  // Fetch roles data
  useEffect(() => {
    fetch('/api/roles')  // Your backend endpoint for roles
      .then((response) => response.json())
      .then((data) => setRolesData(data))
      .catch((error) => console.error('Error fetching roles data:', error));
  }, []);

  // Process user roles data for the Pie chart
  const roleCounts = userData.reduce(
    (acc, user) => {
      acc[user.role] = (acc[user.role] || 0) + 1;
      return acc;
    },
    { 'super-admin': 0, admin: 0, user: 0, other: 0 }
  );

  const userRolesData = [
    { name: 'Super Admin', value: roleCounts['super-admin'] },
    { name: 'Admin', value: roleCounts['admin'] },
    { name: 'User', value: roleCounts['user'] },
    { name: 'Other', value: roleCounts['other'] },
  ];

  // Prepare data for the Bar chart (roles)
  const rolesChartData = rolesData.map(role => ({
    name: role.name,
    count: role.count,
  }));

  return (
    <div>
      <h2>Users by Role</h2>
      <ResponsiveContainer width="100%" height={300}>
        <PieChart>
          <Pie
            data={userRolesData}
            dataKey="value"
            nameKey="name"
            outerRadius={100}
            fill="#8884d8"
            label
          >
            {userRolesData.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={['#ff6f61', '#ffcc66', '#66ccff', '#ff9933'][index % 4]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </ResponsiveContainer>

      <h2>Roles Created</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={rolesChartData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <BarTooltip />
          <Bar dataKey="count" fill="#8884d8" />
          <BarLegend />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default Graph;
