import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Pie, Bar } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, BarElement, CategoryScale, LinearScale } from 'chart.js';

// Register ChartJS components
ChartJS.register(ArcElement, Tooltip, Legend, BarElement, CategoryScale, LinearScale);

const Graph = () => {
  const [userData, setUserData] = useState(null); // Set initial state to null
  const [roleData, setRoleData] = useState(null); // Set initial state to null

  useEffect(() => {
    // Fetch users data
    axios.get('http://localhost:5000/api/users')
      .then(response => {
        const users = response.data;

        // Calculate role distribution for every role created
        const roles = users.reduce((acc, user) => {
          const roleName = user.role.name; // assuming 'role' is populated with the role name
          
          // If the role is Super-Admin, Admin, or User, count them normally
          if (['Super-Admin', 'Admin', 'User'].includes(roleName)) {
            acc[roleName] = (acc[roleName] || 0) + 1;
        } else if ([''].includes(roleName) != ['Super-Admin', 'Admin', 'User'].includes(roleName)){

            // Otherwise, group all other roles under 'Other'
            acc['Other'] = (acc['Other'] || 0) + 1;
          }
          return acc;
        }, {});

        // Set the chart data for the Pie chart
        // Define colors for known roles
const roleColors = {
    'Super-Admin': '#FF6384', // Red
    'Admin': '#36A2EB', // Blue
    'User': '#FFCE56', // Yellow
    'Other': '#808080', // Grey for Other
  };
  
  // Dynamically assign colors based on the roles in the dataset
  const colors = Object.keys(roles).map(role => roleColors[role] || '#4BC0C0'); // Default color if role is not predefined
  
  setUserData({
    labels: Object.keys(roles),
    datasets: [
      {
        label: 'Users by Role',
        data: Object.values(roles),
        backgroundColor: colors,
      },
    ],
  });
  
      })
      .catch(error => console.error('Error fetching users data:', error));

    // Fetch roles data for the Bar chart
    axios.get('http://localhost:5000/api/roles')
      .then(response => {
        const roles = response.data;
        const roleNames = roles.map(role => role.name);
        const roleCounts = new Array(roles.length).fill(1); // Adjust this if you want to represent actual counts

        // Set the chart data for the Bar chart
        setRoleData({
          labels: roleNames,
          datasets: [
            {
              label: 'Roles Created',
              data: roleCounts,
              backgroundColor: '#36A2EB', // Customize color
              borderColor: '#1f7fc7',
              borderWidth: 1,
            },
          ],
        });
      })
      .catch(error => console.error('Error fetching roles data:', error));
  }, []);

  // Only render the charts if the data is available
  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', width: '100%', paddingTop: '50px' }}>
      <h2 style={{ textAlign: 'center', color: '#FFCE56' }}>User Roles Distribution</h2>
      <div style={{ width: '80%', height: '400px', marginBottom: '50px', display: 'flex', justifyContent: 'center' }}>
        {userData ? <Pie data={userData} /> : <p>Loading user data...</p>}
      </div>

      <h2 style={{ textAlign: 'center', color: '#FFCE56' }}>Roles Created</h2>
      <div style={{ width: '80%', height: '400px', display: 'flex', justifyContent: 'center' }}>
        {roleData ? <Bar data={roleData} options={{ responsive: true }} /> : <p>Loading role data...</p>}
      </div>
    </div>
  );
};

export default Graph;
