import React from 'react';
import { Box, List, ListItem, ListItemText, Drawer } from '@mui/material';
import { Link } from 'react-router-dom';
import './Sidebar.css';

const Sidebar = () => {
    return (
        <Drawer variant="permanent" sx={{ width: 240, flexShrink: 0 }}>
            <Box sx={{ width: 240, height: '100%', backgroundColor: '#1976d2' }}>
                <List>
                    <ListItem component={Link} to="/home" className="nav-link"> {/* Correct path to Home */}
                        <ListItemText primary="Home" />
                    </ListItem>
                    <ListItem component={Link} to="/users" className="nav-link">
                        <ListItemText primary="Users" />
                    </ListItem>
                    <ListItem component={Link} to="/roles" className="nav-link">
                        <ListItemText primary="Roles" />
                    </ListItem>
                </List>
            </Box>
        </Drawer>
    );
};

export default Sidebar;
