import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Pie, Bar } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, BarElement, CategoryScale, LinearScale } from 'chart.js';

// Register ChartJS components
ChartJS.register(ArcElement, Tooltip, Legend, BarElement, CategoryScale, LinearScale);

const Graph = () => {
  const [userData, setUserData] = useState({});
  const [roleData, setRoleData] = useState({});

  useEffect(() => {
    // Fetch users data
    axios.get('http://localhost:5000/api/users')
      .then(response => {
        const users = response.data;

        // Calculate role distribution
        const roles = users.reduce((acc, user) => {
          const roleName = user.role.name; // assuming 'role' is populated with the role name
          acc[roleName] = (acc[roleName] || 0) + 1;
          return acc;
        }, {});

        setUserData({
          labels: Object.keys(roles),
          datasets: [
            {
              label: 'Users by Role',
              data: Object.values(roles),
              backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'], // Customize colors
            },
          ],
        });
      })
      .catch(error => console.error('Error fetching users data:', error));

    // Fetch roles data
    axios.get('http://localhost:5000/api/roles')
      .then(response => {
        const roles = response.data;
        const roleNames = roles.map(role => role.name);
        const roleCounts = new Array(roles.length).fill(1); // You can adjust this to represent counts if needed

        setRoleData({
          labels: roleNames,
          datasets: [
            {
              label: 'Roles Created',
              data: roleCounts,
              backgroundColor: '#36A2EB', // Customize color
              borderColor: '#1f7fc7',
              borderWidth: 1,
            },
          ],
        });
      })
      .catch(error => console.error('Error fetching roles data:', error));
  }, []);

  return (
    <div style={{ width: '80%', margin: 'auto', paddingTop: '50px' }}>
      <h2 style={{ textAlign: 'center', color: '#FFCE56' }}>User Roles Distribution</h2>
      <div style={{ height: '400px' }}>
        <Pie data={userData} />
      </div>

      <h2 style={{ textAlign: 'center', color: '#FFCE56', marginTop: '50px' }}>Roles Created</h2>
      <div style={{ height: '400px' }}>
        <Bar data={roleData} options={{ responsive: true }} />
      </div>
    </div>
  );
};

export default Graph;
