import React from 'react';
import { Box, List, ListItem, ListItemText, Drawer } from '@mui/material';
import { Link } from 'react-router-dom';
import './Sidebar.css';

const Sidebar = () => {
  return (
    <Box sx={{ position: 'relative', height: '100vh' }}>
      {/* Background Video */}
      <video autoPlay loop muted className="background-video">
            <source src="/video.mp4" type="video/mp4" />
            <source src="/video.webm" type="video/webm" />
            <source src="/video.ogv" type="video/ogg" />
            Your browser does not support the video tag.
        </video>

      <Drawer variant="permanent" sx={{ width: 240, flexShrink: 0 }}>
        <Box sx={{ width: 240, height: '100%', backgroundColor: 'rgba(0, 0, 0, 0.7)' }}>
          <List>
            <ListItem component={Link} to="/home" className="nav-link">
              <ListItemText primary="Home" />
            </ListItem>
            <ListItem component={Link} to="/about" className="nav-link">
              <ListItemText primary="About" />
            </ListItem>
            <ListItem component={Link} to="/contact" className="nav-link">
              <ListItemText primary="Contact" />
            </ListItem>
          </List>
        </Box>
      </Drawer>
    </Box>
  );
};

export default Sidebar;
