import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Button, TextField, MenuItem, Select, InputLabel, FormControl, Grid, Box, Typography } from '@mui/material';

// Helper functions for API interaction
const fetchRoles = async () => {
  const res = await fetch('http://localhost:5000/api/roles');
  if (!res.ok) throw new Error('Failed to fetch roles');
  return res.json();
};

const createOrUpdateRole = async ({ roleId, roleData }) => {
  const url = roleId
    ? `http://localhost:5000/api/roles/${roleId}`
    : 'http://localhost:5000/api/roles';
  const method = roleId ? 'PUT' : 'POST';

  const res = await fetch(url, {
    method,
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(roleData),
  });
  if (!res.ok) throw new Error('Failed to save role');
  return res.json();
};

const deleteRole = async (roleId) => {
  const res = await fetch(`http://localhost:5000/api/roles/${roleId}`, {
    method: 'DELETE',
  });
  if (!res.ok) throw new Error('Failed to delete role');
  return roleId;
};

const Roles = () => {
  const queryClient = useQueryClient();
  const [formData, setFormData] = useState({
    name: '',
    permissions: [],
  });
  const [editingRole, setEditingRole] = useState(null);

  const permissionsList = ['read', 'write', 'delete', 'manage'];

  // Fetch roles
  const { data: roles, isLoading, isError } = useQuery({
    queryKey: ['roles'],
    queryFn: fetchRoles,
  });

  // Mutation for creating/updating roles
  const saveRoleMutation = useMutation({
    mutationFn: createOrUpdateRole,
    onSuccess: () => {
      queryClient.invalidateQueries(['roles']); // Refetch roles after successful mutation
      resetForm();
    },
  })

  // Mutation for deleting roles
  const deleteRoleMutation = useMutation({
    mutationFn: deleteRole,
    onSuccess: () => {
      queryClient.invalidateQueries(['roles']); // Refetch roles after deletion
    },
  })

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handlePermissionsChange = (e) => {
    setFormData({ ...formData, permissions: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    saveRoleMutation.mutate({
      roleId: editingRole?._id,
      roleData: formData,
    });
  };

  const handleEdit = (role) => {
    setEditingRole(role);
    setFormData({
      name: role.name,
      permissions: role.permissions || [],
    });
  };

  const handleDelete = (id) => {
    deleteRoleMutation.mutate(id);
  };

  const resetForm = () => {
    setFormData({
      name: '',
      permissions: [],
    });
    setEditingRole(null);
  };

  if (isLoading) return <Typography>Loading...</Typography>;
  if (isError) return <Typography>Error loading roles</Typography>;

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        {editingRole ? 'Edit Role' : 'Create a New Role'}
      </Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Role Name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              fullWidth
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth required>
              <InputLabel>Permissions</InputLabel>
              <Select
                multiple
                name="permissions"
                value={formData.permissions}
                onChange={handlePermissionsChange}
                label="Permissions"
              >
                {permissionsList.map((permission, index) => (
                  <MenuItem key={index} value={permission}>
                    {permission}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
        </Grid>
        <Button type="submit" variant="contained" color="primary" sx={{ marginTop: '20px' }}>
          {editingRole ? 'Update Role' : 'Create Role'}
        </Button>
      </form>

      <Typography variant="h4" gutterBottom sx={{ marginTop: 5 }}>
        Role List
      </Typography>
      <Box>
        {roles.length > 0 ? (
          <Grid container spacing={3}>
            {roles.map((role) => (
              <Grid item xs={12} sm={6} key={role._id}>
                <Box sx={{ border: '1px solid #ccc', padding: 2 }}>
                  <Typography variant="h6">{role.name}</Typography>
                  <p>
                    <strong>Permissions:</strong> {(role.permissions || []).join(', ')}
                  </p>
                  <Button variant="contained" color="secondary" onClick={() => handleEdit(role)}>
                    Edit
                  </Button>
                  <Button
                    variant="contained"
                    color="error"
                    onClick={() => handleDelete(role._id)}
                    sx={{ marginLeft: '15px' }}
                  >
                    Delete
                  </Button>
                </Box>
              </Grid>
            ))}
          </Grid>
        ) : (
          <p>No roles found.</p>
        )}
      </Box>
    </div>
  );
};

export default Roles;
